echo "Episode,Step,X,Y,Heading,Steering,Speed,Action,Reward,Crashed,On Track,Progress,Waypoint Index,Track_Len,Time,Status,Laps,off_track_penalty,reward_on_track,reward_max_speed,reward_min_steering,reward_dir_steering,reward_prog_step,distance_from_center,track_direction_1,track_direction_2,track_direction_3,direction_diff_ratio,wp0_X,wpo_Y,pp_X,pp_Y,np1_X,np1_Y,np2_X,np2_Y,np3_X,np3_Y" > $1.csv
egrep "^(Reset agent finished|SIM_TRACE_LOG:|rewards:)" $1 | sed 's/Reset agent finished/rewards:/g' | tr '\n' '|' | sed 's/|rewards/#rewards/g' | tr '#' '\n' | sed -E 's/^rewards:([^|]*)\|(.+)$/\2,\1/g' | egrep "^SIM_TRACE_LOG:" | sed 's/SIM_TRACE_LOG://g' >> $1.csv